mapper.select:
SELECT
,
(select shop_name from shop s where s.shop_id=sr.shop_id ) shopName,
(select sum(stock_quantity) from stock_record_item sri where sri.stock_record_id=sr.id) stockQuantityTotal
from stock_record sr where sr.shop_id in (select su.shop_id from shop_user su where su.user_id=#{userId})
and sr.shop_id=#{shopId}
and sr.id=#{id}

    and EXISTS (select 1 from stock_record_item sri,shop_sku ss where sri.shop_sku_id=ss.shop_sku_id and ss.shop_sku like concat('%',#{shopSku},'%') and sri.stock_record_id=sr.id)

    and EXISTS (select 1 from stock_record_item sri,shop_sku ss,sku where sri.shop_sku_id=ss.shop_sku_id and ss.sku_id=sku.sku_id and sku.sku like concat('%',#{sku},'%') and sri.stock_record_id=sr.id)
ORDER BY sr.create_time desc
